package ud4;

public class Ej1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Constantes operaciones
		int Primervalor=25, SegundoValor=5;
		//Constantes Resultados
		int SumaDeValores,RestaDeValores,MultiplicacionDeValores,DivisionDeValores,ResiduoDeValores;
		
		//Resultado Suma
		SumaDeValores=Primervalor+SegundoValor;
		System.out.println(SumaDeValores);
		
		//Resultado resta
		RestaDeValores=Primervalor+SegundoValor;
		System.out.println(RestaDeValores);
		
		//Resultado Multiplicacion
		MultiplicacionDeValores=Primervalor*SegundoValor;
		System.out.println(MultiplicacionDeValores);
		
		//Resultado Division
		DivisionDeValores=Primervalor/SegundoValor;
		System.out.println(DivisionDeValores);
		
		//Residuo Division
		ResiduoDeValores=Primervalor%SegundoValor;
		System.out.println(ResiduoDeValores);
		
		
	}

}
