package ud4;

public class Ej4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int N=2;
		
		//Mostamos el valor de N
		System.out.println("Valor de N = 1 = " + N);
		//Sumamos 77 al valor de N
		System.out.println("Incrementacion de N + 77 = " + (N + 77));
		//Restamos 3 al valor de N
		System.out.println("Decrementar de N - 3 = " + (N - 3));
		//Multiplicamos 2 al valor de N
		System.out.println("Duplicar de N * 2 = " + (N * 2 ));
	}

}
